defmodule Bitcoin.ChainParams.Testnet do
  @moduledoc """
    List of constants associated with the Bitcoin testnet (v3).

    https://github.com/bitcoin/bitcoin/blob/master/src/chainparams.cpp
  """

  defmacro __using__(_opts) do
    quote do

      @genesis_block "" |> Binary.from_hex |> Bitcoin.Protocol.Messages.Block.parse

      @genesis_hash "000000000933ea01ad0ee984209779baaec3ced90fa3f408719526f8d77f4943" |> Bitcoin.Util.hex_to_hash

      @network_magic_bytes <<0xF4, 0xE5, 0xF3, 0xF4>>

      @default_listen_port 18333

      @default_rpc_port 18332

      # bytes to append before base56check encoding
      @address_prefix [
        public: 111,
        script: 196,
        private:  239,
      ]

      @dns_seeds [
        { "bitcoinsv.io", 'testnet-seed.bitcoinsv.io' }, # Bitcoin SV seeder
        { "bitprim.org", 'testnet-seed.bitprim.org' }, # Bitprim
        { "deadalnix.me", 'testnet-seed.deadalnix.me' }, # Amaury SÃ‰CHET
        { "criptolayer.net", 'testnet-seeder.criptolayer.net' }, # criptolayer.net
      ]

      # BIPs activation conditions

      # P2SH
      @bip16_switch_time 1333238400

      # Strict DER
      @bip66_height 330776

    end
  end
end
